sys_script_include.script:
/**
 * @class RecordDeleter
 * @description 
 * @since 24/02/2026
 * @date  24/02/2026
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class RecordDeleter {

	/**
     * 
     */
    constructor() {
        this.LOG_HANDLER = new LoggingHandler();
    }

    /**
     * 
     */
    recordDelete(source, reason, table, sysId, workflow) {
        workflow = workflow || false;
        let result = '';

        if (UtilityFunctions.hasToExecute(table)) {
            const params = {
                'table': table,
                'sysId': sysId,
                'workflow': UtilityFunctions.getBooleanValue(workflow),
                'result': ''

            };

            const script = ScriptBuider.getScriptRecordDelete();

            result = new ScriptEvaluator().executScript('global', script, params);

            this.LOG_HANDLER.info(source, 'Record successfully deleted', reason, {
                'targetRecord': result,
                'targetTable': table
            });
        } else {
            this.LOG_HANDLER.error(source, 'Record delete failed', reason, table, {
                'sysId': sysId
            });
        }

        return result;
    }
}
