sys_script_include.script:
/**
 * @class AppInstallAdjustScriptIncludes
 * @description Helper script
 * @since 32/02/2026
 * @date  32/02/2026
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */

class AppInstallAdjustScriptIncludes {

    adjustScripts(installer) {
        const query = 'name=TableUtils^sys_scope=global^active=true';
        const properties = {
            'access': 'public'
        };

        installer.recordUpdateByQuery('TableUtils Access change to public',
            'sys_script_include', query, properties, false);
    }

}
