class Installer {

    /**
     * 
     */
    // eslint-disable-next-line
    recordInsert(reason, table, properties, workflow) {
        workflow = workflow || false;

        const params = {
            'table': table,
            'properties': JSON.stringify(properties),
            'workflow': UtilityFunctions.getBooleanValue(workflow),
            'result': ''
        };

        const script = ScriptBuider.getScriptRecordInsert();

        return new ScriptEvaluator().executScript('global', script, params);
    }


    /**
     * 
     */
    // eslint-disable-next-line
    recordUpdate(reason, table, sysId, field, value, workflow) {
        workflow = workflow || false;

        const properties = {
            'field': field,
            'value': value
        };

        const params = {
            'reason': reason,
            'table': table,
            'column': 'sys_id',
			'identifier': sysId,
            'properties': JSON.stringify(properties),
            'workflow': UtilityFunctions.getBooleanValue(workflow),
            'result': ''
        };

        const script = ScriptBuider.getScriptRecordUpdate();

        return new ScriptEvaluator().executScript('global', script, params);
    }

    /**
     * @description Updates records from mention table based on mention query.
     *
     * @param {String} reason
     * @param {String} table The table to be used.
     * @param {String} query Encoded query
     * @param {Object} properties property object
     * @param {Boolean} workFlow Enables or disables the running of business rules
     *
     * @returns {String}  sys_id of the updated record. Returns null if the update fails.
     */
    // eslint-disable-next-line
    recordUpdateByQuery(reason, table, query, properties, workflow) {
        workflow = workflow || false;

        const params = {
            'reason': reason,
            'table': table,
            'query': query,
            'properties': properties,
            'workflow': UtilityFunctions.getBooleanValue(workflow),
            'result': ''
        };

        const script = ScriptBuider.getScriptRecordUpdateByQuery();

        return new ScriptEvaluator().executScript('global', script, params);
    }

    /**
     * 
     */
    recordDelete(reason, table, sysId, workflow) {
        workflow = workflow || false;

        const params = {
            'table': table,
            'sysId': sysId,
            'workflow': UtilityFunctions.getBooleanValue(workflow),
			'result': ''

        };

        const script = ScriptBuider.getScriptRecordDelete();

        return new ScriptEvaluator().executScript('global', script, params);
    }

    /**
     * 
     */
    // eslint-disable-next-line
    dictionaryRecordInsert(reason, table, element, field, value, workflow) {
        workflow = workflow || false;

        const properties = {
            'element': element,
            'field': field,
            'value': value
        };

        const params = {
            'reason': reason,
            'table': table,
            'properties': properties,
            'workflow': UtilityFunctions.getBooleanValue(workflow),
            'result': ''
        };

        const script = ScriptBuider.getScriptRecordInsert();

        return new ScriptEvaluator().executScript('global', script, params);
    }

    /**
     * 
     */
    // eslint-disable-next-line
    dictionaryOverrideRecordInsert(reason, table, element, override, field, value, workflow) {
        workflow = workflow || false;

        const properties = {
            'element': element,
            'field': field,
            'override': override,
            'value': value
        };

        var params = {
            'reason': reason,
            'table': table,
            'properties': properties,
            'workflow': UtilityFunctions.getBooleanValue(workflow),
            'result': ''
        };

        const script = ScriptBuider.getScriptRecordInsert();

        return new ScriptEvaluator().executScript('global', script, params);
    }

    /**
     * 
     */
    // eslint-disable-next-line
