 */
class Installer {

    /**
     * 
     */
    // eslint-disable-next-line
    recordInsert(reason, table, properties, workflow) {
        workflow = workflow || false;
		const LOG_HANDLER = new LoggingHandler();
		let result = '';

        if (this._hasToExcecude(table)) {
            const params = {
                'table': table,
                'properties': JSON.stringify(properties),
                'workflow': UtilityFunctions.getBooleanValue(workflow),
                'result': ''
            };

            const script = ScriptBuider.getScriptRecordInsert();

            result = new ScriptEvaluator().executScript('global', script, params);

			LOG_HANDLER.info('Successfully');
        } else {
			LOG_HANDLER.error('Read only table!');
        }

		return result;
    }


    /**
     * 
     */
    // eslint-disable-next-line
    recordUpdate(reason, table, sysId, field, value, workflow) {
		workflow = workflow || false;
		const LOG_HANDLER = new LoggingHandler();
        let result = '';

        if (this._hasToExcecude(table)) {
            const properties = {
                'field': field,
                'value': value
            };

            const params = {
                'reason': reason,
                'table': table,
                'column': 'sys_id',
                'identifier': sysId,
                'properties': JSON.stringify(properties),
                'workflow': UtilityFunctions.getBooleanValue(workflow),
                'result': ''
            };

            const script = ScriptBuider.getScriptRecordUpdate();

            result = new ScriptEvaluator().executScript('global', script, params);

			LOG_HANDLER.info('Successfully');
        } else {
            LOG_HANDLER.error('Read only table!');
        }

		return result;
    }

    /**
     * @description Updates records from mention table based on mention query.
     *
     * @param {String} reason
     * @param {String} table The table to be used.
     * @param {String} query Encoded query
     * @param {Object} properties property object
     * @param {Boolean} workFlow Enables or disables the running of business rules
     *
     * @returns {String}  sys_id of the updated record. Returns null if the update fails.
     */
    // eslint-disable-next-line
    recordUpdateByQuery(reason, table, query, properties, workflow) {
        workflow = workflow || false;
		const LOG_HANDLER = new LoggingHandler();
		let result = '';


        if (this._hasToExcecude(table)) {
            const params = {
                'reason': reason,
                'table': table,
                'query': query,
                'properties': JSON.stringify(properties),
                'workflow': UtilityFunctions.getBooleanValue(workflow),
                'result': ''
            };

            const script = ScriptBuider.getScriptRecordUpdateByQuery();

            result = new ScriptEvaluator().executScript('global', script, params);

			LOG_HANDLER.info('Read only table!');
        } else {
           LOG_HANDLER.error('Read only table!');
        }

		return result;
    }

    /**
     * 
     */
    recordDelete(reason, table, sysId, workflow) {
        workflow = workflow || false;
		const LOG_HANDLER = new LoggingHandler();
		let result = '';

        if (this._hasToExcecude(table)) {
            const params = {
                'table': table,
                'sysId': sysId,
                'workflow': UtilityFunctions.getBooleanValue(workflow),
                'result': ''

            };

            const script = ScriptBuider.getScriptRecordDelete();

            result = new ScriptEvaluator().executScript('global', script, params);

			LOG_HANDLER.info('Successfully');
        } else {
           LOG_HANDLER.error('Read only table!');
        }

		return result;
    }

    /**
     * 
     */
    // eslint-disable-next-line
    dictionaryOverrideRecordInsert(reason, element, override, field, value, workflow) {
        workflow = workflow || false;

        const properties = {
            'element': element,
            'field': field,
            'override': override,
            'value': value
        };

        var params = {
            'reason': reason,
            'table': 'sys_dictionary_override',
            'properties': properties,
            'workflow': UtilityFunctions.getBooleanValue(workflow),
            'result': ''
        };

        const script = ScriptBuider.getScriptRecordInsert();

        return new ScriptEvaluator().executScript('global', script, params);
    }

    /**
     * 
     */
    // eslint-disable-next-line
    dictionaryRecordUpdate(reason, element, field, value, workflow) {
        workflow = workflow || false;

        const query = '';

        const properties = {
            'field': field,
            'value': value
        };

        var params = {
            'reason': reason,
            'table': 'sys_dictionary',
            'properties': properties,
            'workflow': UtilityFunctions.getBooleanValue(workflow),
            'result': ''
        };

        const script = ScriptBuider.getScriptRecordUpdateByQuery();

        return new ScriptEvaluator().executScript('global', script, params);
    }

    /**
     * 
     */
    // eslint-disable-next-line
    dictionaryOverrideRecordUpdate(reason, element, override, field, value, workflow) {
        workflow = workflow || false;

        const properties = {
            'field': field,
            'value': value
        };

        var params = {
            'reason': reason,
            'table': 'sys_dictionary_override',
            'properties': properties,
            'workflow': UtilityFunctions.getBooleanValue(workflow),
            'result': ''
        };

        const script = ScriptBuider.getScriptRecordUpdateByQury();

        return new ScriptEvaluator().executScript('global', script, params);
    }

	/**
	 * 
	 */
	_hasToExcecude(table) {
		return !Constants.BLACK_LIST_TABLES.includes(table);
	}

}
