sys_script_include.script:
/**
 * @class UtilityFunctions
 * @description Provides a set of helper functions.
 * @since 19/02/2026
 * @date  19/02/2026
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class UtilityFunctions {


    static getBooleanValue(value) {
        const TRUE = 1;
        let booleanValue = false;

        if ((value === true) || (value === 'true') || (value === TRUE)) {
            booleanValue = true;
        }

        return booleanValue;
    }

    /**
     * 
     */
    static hasToExecute(tableName) {
        const table = new global.TableUtils(tableName);

        return table.tableExists() && !Constants.BLACK_LIST_TABLES.includes(tableName);
    }

    /**
     * 
     */
    static getBaseTable(tableName) {
        const table = new global.TableUtils(tableName);

        return table.getAbsoluteBase();
    }

}
