sys_script_include.script:
/**
 * @class LoggingHandler
 * @description 
 * @since 19/02/2026
 * @date  19/02/2026
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class LoggingHandler {

    /**
     * Inserts a info message
	 * 
	 * @param {String} source source application
	 * @param {String} message log message
	 * @param {String} reason insert/update reason 
	 * @param {Object} targetRecord record table and record sys_id
	 * 
     */
    info(source, message, reason, targetRecord) {
		reason = reason || '';
        targetRecord = targetRecord || {
            'targetRecord': '',
            'targetTable': ''
        };

        const INFO = 0;
        const record = new GlideRecord('x_984628_deploye_0_deployer_log');

        record.initialize();
        record.setValue('level', INFO);
		record.setValue('source', source);
        record.setValue('message', message);
		record.setValue('reason', reason);
        record.setValue('target_record', targetRecord.targetRecord);
        record.setValue('target_table', targetRecord.targetTable);
        record.insert();
    }

    /**
     * Inserts a error message
	 * 
	 * @param {String} source source application
	 * @param {String} message log message
	 * @param {String} reason insert/update reason 
	 * @param {String} table table
	 * @param {Object} properties properties
	 * 
     */
    error(source, message, reason, table, properties) {
        const ERROR = 2;
        const record = new GlideRecord('x_984628_deploye_0_deployer_log');

        record.initialize();
        record.setValue('level', ERROR);
        record.setValue('source', source);
		record.setValue('reason', reason);
		record.setValue('table', table);
		record.setValue('properties', JSON.stringify(properties));
        record.insert();
    }
}
