class LoggingHandler {


    info(message) {
        const record = new GlideRecord('');

        record.initialize();


        record.insert();
    }

	warn(message) {
        const record = new GlideRecord('');

        record.initialize();


        record.insert();
	}

	error(message) {
        const record = new GlideRecord('');

        record.initialize();


        record.insert();
	}


}
