sys_script_include.script:
/**
 * @class ScriptEvaluator
 * @description 
 * @since 19/02/2026
 * @date  19/02/2026
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class ScriptEvaluator {


    // WARNING: Using this function might be considered as a violation of security policies.
    // Please make sure any use is based on conscious choice and careful assessment.
    /**
     * @param {String} sourceScript
     * @param {Object} params
     */
    executScript(scope, sourceScript, params) {
        const LIMIT = 1;
        let evaluatedValue = null;
        const evaluator = new GlideScopedEvaluator();

        const gr = new GlideRecord('sys_script_include');
        gr.addQuery('sys_scope', scope);
        // // additional protection against accidental update
        gr.addQuery('sys_policy', 'read');
        gr.setLimit(LIMIT);
        gr.query();

        if (gr.next()) {

            gr.setValue('script', sourceScript);

            try {
                evaluatedValue = evaluator.evaluateScript(gr, 'script', params);
                evaluatedValue = evaluator.getVariable('result');
            } catch (e) {
                throw new Error(e);
            }
        }

        return evaluatedValue;
    }
}
