sys_script_include.script:
/**
 * @class QueryBuilder
 * @description 
 * @since 24/02/2026
 * @date  24/02/2026
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class QueryBuilder  {

    /**
     * Returns an encoded query based on the given parameters
     *
	 * @param {String} baseTable base table name
     * @param {String} name table name
     * @param {String} element field name
     *
     * @returns {Array} array of encoded query
     */
	static findDictionaryOverride(baseTable, name, element) {
       const builder = new StringBuilder();

        builder.append("base_table=" + baseTable);
		builder.append("^name=" + name);
		builder.append("^element=" + element);

        return builder.toString();
	}

}
