sys_script_include.script:
/**
 * @class RecordUpdater
 * @description 
 * @since 24/02/2026
 * @date  24/02/2026
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class RecordUpdater {

    /**
     * 
     */
    constructor() {
        this.LOG_HANDLER = new LoggingHandler();
    }

    /**
     * 
     */
    // eslint-disable-next-line
    recordUpdate(source, reason, table, sysId, field, value, workflow) {
        workflow = workflow || false;
        let result = '';

        if (UtilityFunctions.hasToExecute(table)) {
            const properties = {
                'field': field,
                'value': value
            };

            const params = {
                'reason': reason,
                'table': table,
                'column': 'sys_id',
                'identifier': sysId,
                'properties': JSON.stringify(properties),
                'workflow': UtilityFunctions.getBooleanValue(workflow),
                'result': ''
            };

            const script = ScriptBuider.getScriptRecordUpdate();

            result = new ScriptEvaluator().executScript('global', script, params);

            this._processResult(result, source, reason, table, properties);

        } else {
            this.LOG_HANDLER.error(source, 'Record update failed', reason, table, {
                'sysId': sysId,
                'field': field
            });
        }

        return result;
    }

    /**
     * @description Updates records from mention table based on mention query.
     *
     * @param {String} reason 
     * @param {String} table The table to be used.
     * @param {String} query Encoded query
     * @param {Object} properties property object
     * @param {Boolean} workFlow Enables or disables the running of business rules
     *
     * @returns {String}  sys_id of the updated record. Returns null if the update fails.
     */
    // eslint-disable-next-line
    recordUpdateByQuery(source, reason, table, query, properties, workflow) {
        workflow = workflow || false;
        let result = '';


        if (UtilityFunctions.hasToExecute(table)) {
            const params = {
                'table': table,
                'query': query,
                'properties': JSON.stringify(properties),
                'workflow': UtilityFunctions.getBooleanValue(workflow),
                'result': ''
            };

            const script = ScriptBuider.getScriptRecordUpdateByQuery();

            result = new ScriptEvaluator().executScript('global', script, params);

            this._processResult(result, source, reason, table, properties);

        } else {
            this.LOG_HANDLER.error(source, 'Record update failed', reason, table, properties);
        }

        return result;
    }

    /**
     * 
     */
    // eslint-disable-next-line
    dictionaryOverrideRecordUpdate(source, reason, name, element, override, field, value, workflow) {
        workflow = workflow || false;
        const properties = {};
        let result = '';
        const query = QueryBuilder.findDictionaryOverride(UtilityFunctions.getBaseTable(name), name, element);

        if (UtilityFunctions.hasToExecute(name)) {

            properties[override] = true;
            properties[field] = value;

            gs.debug('[RecordUpdater][dictionaryOverrideRecordUpdate] properties ==>' +
                JSON.stringify(properties));

            var params = {
                'table': 'sys_dictionary_override',
                'query': query,
                'properties': JSON.stringify(properties),
                'workflow': UtilityFunctions.getBooleanValue(workflow),
                'result': ''
            };

            const script = ScriptBuider.getScriptRecordUpdateByQuery();

            result = new ScriptEvaluator().executScript('global', script, params);

            this._processResult(result, source, reason, 'sys_dictionary_override', properties);

        } else {
            this.LOG_HANDLER.error(source, 'Record update failed', reason,
                'sys_dictionary_override', properties);
        }

        return result;
    }

    /**
     * 
     */
    // eslint-disable-next-line
    dictionaryRecordUpdate(source, reason, element, field, value, workflow) {
        workflow = workflow || false;

        const query = '';

        const properties = {
            'field': field,
            'value': value
        };

        var params = {
            'reason': reason,
            'table': 'sys_dictionary',
            'properties': properties,
            'workflow': UtilityFunctions.getBooleanValue(workflow),
            'result': ''
        };

        const script = ScriptBuider.getScriptRecordUpdateByQuery();

        return new ScriptEvaluator().executScript('global', script, params);
    }

    _processResult(result, source, reason, table, properties) {
        if (result) {
            this.LOG_HANDLER.info(source, 'Record successfully inserted', reason, {
                'targetRecord': result,
                'targetTable': table
            });
        } else {
            this.LOG_HANDLER.error(source, 'Record update failed', reason, table, properties);
        }
    }

}
