sys_script_include.script:
/**
 * @class RecordInserter
 * @description 
 * @since 24/02/2026
 * @date  24/02/2026
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class RecordInserter {

    /**
     * 
     */
    constructor() {
        this.LOG_HANDLER = new LoggingHandler();
    }

    /**
     * 
     */
    recordInsert(source, reason, table, properties, workflow) {
        workflow = workflow || false;
        let result = '';

        if (UtilityFunctions.hasToExecute(table)) {
            const params = {
                'table': table,
                'properties': JSON.stringify(properties),
                'workflow': UtilityFunctions.getBooleanValue(workflow),
                'result': ''
            };

            const script = ScriptBuider.getScriptRecordInsert();

            result = new ScriptEvaluator().executScript('global', script, params);

            this._processResult(result, source, reason, table, properties);

        } else {
            this.LOG_HANDLER.error(source, 'Record insert failed', reason, table, properties);
        }

        return result;
    }

    _processResult(result, source, reason, table, properties) {
        if (result) {
            this.LOG_HANDLER.info(source, 'Record successfully inserted', reason, {
                'targetRecord': result,
                'targetTable': table
            });
        } else {
            this.LOG_HANDLER.error(source, 'Record insert failed', reason, table, properties);
        }
    }

}
