class ScriptBuider {

    /**
     * 
     */
    static getScriptRecordInsert() {
        const builder = new StringBuilder();

        builder.append("var result = '';");
        builder.append("var record = new GlideRecord(table);");
        builder.append("record.initialize();");
        builder.append("var obj = JSON.parse(properties);");

        builder.append("for (var property in obj) { var value = obj[property];");
        builder.append("record.setValue(property, value); }");

        builder.append("record.setWorkflow(workflow);");
        builder.append("result = record.insert();");

        return builder.toString();
    }

    /**
     * 
     */
    static getScriptRecordUpdate() {
        const builder = new StringBuilder();

        builder.append("var result = '';");
        builder.append("var record = new GlideRecord(table);");
        builder.append("record.addQuery('sys_id', sysId);");
        builder.append("record.setLimit(1);");
        builder.append("record.setWorkflow(workflow);");
        builder.append("record.autoSysFields(false);");
        builder.append("record.query();");
        builder.append("if (record.next()) { record[field] = value; result = record.update(reason); }");

        return builder.toString();
    }

    /**
     * 
     */
    static getScriptRecordDelete() {
        const builder = new StringBuilder();

        builder.append("var result = false;");
        builder.append("var record = new GlideRecord(table);");
        builder.append("record.addQuery('sys_id', sysId);");
        builder.append("record.setLimit(1);");
        builder.append("record.setWorkflow(workflow);");
        builder.append("record.autoSysFields(false);");
        builder.append("record.query();");
        builder.append("if (record.next()) { record.deleteRecord(); }");

        return builder.toString();
    }

