sys_script_include.script:
/**
 * @class ScriptBuider
 * @description Helper Script to decodes script to text
 * @since 19/02/2026
 * @date  19/02/2026
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class ScriptBuider {

    /**
     * @description Returns an script decoded to text.
     *
     * @returns {String} decoded script
     */
    static getScriptRecordInsert() {
        const builder = new StringBuilder();

        builder.append("var result = '';");
        builder.append("var obj = JSON.parse(properties);");
        builder.append("var record = new GlideRecord(table);");
        builder.append("record.initialize();");

        builder.append("for (var property in obj) {");
        builder.append("var value = obj[property];");
        builder.append("if (value && typeof value !== 'function') {");
        builder.append("record[property] = value;");
        builder.append("}");
        builder.append("}");

        builder.append("record.setWorkflow(workflow);");
        builder.append("result = record.insert();");

        return builder.toString();
    }

    /**
     * @description Returns an script decoded to text.
     *
     * @returns {String} decoded script
     */
    static getScriptRecordUpdate() {
        const builder = new StringBuilder();

        builder.append("var result = '';");
        builder.append("var obj = JSON.parse(properties);");
        builder.append("var record = new GlideRecord(table);");

        builder.append("if (record.get(column, identifier)) {");
        builder.append("for (var property in obj) {");
        builder.append("var value = obj[property];");
        builder.append("if (value && typeof value !== 'function') {");
        builder.append("record[property] = value;");
        builder.append("}");
        builder.append("}");
        builder.append("}");

        builder.append("record.setWorkflow(workflow);");
        builder.append("result = record.update();");

        return builder.toString();
    }

    /**
     * @description Returns an script decoded to text.
     *
     * @returns {String} decoded script
     */
    static getScriptRecordUpdateByQuery() {
        const builder = new StringBuilder();

        builder.append("var result = '';");
        builder.append("var obj = JSON.parse(properties);");
        builder.append("var record = new GlideRecord(table);");
        builder.append("record.addEncodedQuery(query);");
        builder.append("record.setLimit(1);");
        builder.append("record.query();");

        builder.append("if (record.next()) {");
        builder.append("for (var property in obj) {");
        builder.append("var value = obj[property];");
        builder.append("if (value && typeof value !== 'function') {");
        builder.append("record[property] = value;");
        builder.append("}");
        builder.append("}");
        builder.append("}");

        builder.append("record.setWorkflow(workflow);");
        builder.append("result = record.update();");

        return builder.toString();
    }

    /**
     * @description Returns an script decoded to text.
     *
     * @returns {String} decoded script
     */
    static getScriptRecordDelete() {
        const builder = new StringBuilder();

        builder.append("var result = false;");
        builder.append("var record = new GlideRecord(table);");
        builder.append("record.addQuery('sys_id', sysId);");
        builder.append("record.setLimit(1);");
        builder.append("record.setWorkflow(workflow);");
        builder.append("record.query();");
        builder.append("if (record.next()) {");
        builder.append("result = record.deleteRecord();");
        builder.append("}");

        return builder.toString();
    }

}
