sys_script_include.script:
/**
 * @class StringBuilder
 * @description The StringBuilder provides methods to append, replace, delete and insert from charsequence.
 * @since 09/06/2023
 * @date  09/06/2023
 * @version 1.0.0
 * @author Thomas Grensemann E-Mail: <thomas.grensemann@t-systems.com>
 *
 */
class StringBuilder {

    /**
     * @description constructor
     *
     * @param {String} _string
     */
    constructor(_string) {
        this.resultString = [];
        this.string = _string;
    }

    /**
     * @description Appends the string to the sequence
     *
     * @param {String} string char sequence
     */
    append(string) {
        this.resultString.push(string);
        return this;
    }

    /**
     * @description Replaces the char at the specified position in this sequence.
     *
     * @param {number} index Index of char
     * @param {character} character char sequence
     */
    replaceAt(index, character) {
        const FROM = 0;

        this.resultString.push(this.string.substr(FROM, index) +
            character + this.string.substr(index + character.length));

        return this;
    }

    /**
     * @description Removes the char at the specified position in this sequence.
     *
     * @param {number} index  Index of char to remove
     * @returns {object} current object this
     */
    deleteCharAt(index) {
		const COUNT = 1;

        // eslint-disable-next-line 
        index = index - 1;
        // eslint-disable-next-line 
        if (index >= 0) {
            var array = this.string.split('');

            array.splice(index, COUNT);
            array = array.join('');

            this.resultString.push(array);
        } else {
            this.resultString.push(this.string);
        }

        return this;
    }

    /**
     * @description Inserts the string representation of the char argument into this sequence.
     *
     * @param {number}  index  Index of char
     * @param {character} character char sequence
     *
     * @returns {object} current object this
     */
    insert(offset, insert) {
        const FROM = 0;

// eslint-disable-next-line 
        if (offset > 0) {
            this.resultString.push(this.string.substring(FROM, offset) +
                insert + this.string.substring(offset, this.length));
        } else {
            this.resultString.push(insert + this.string);
        }
        return this;
    }

    /**
     * @description Returns a string representing the data in this sequence.
     */
    toString() {
        var result = this.resultString.join('');

		// eslint-disable-next-line 
        return new String(result);
    }
}
